	is_fixed <- inherits(pattern, "stringr_fixed")

	string <- Vectorize(sub, c("pattern", "replacement", "x"), USE.NAMES = FALSE)(
		pattern, replacement, x = string, perl = !is_fixed, fixed = is_fixed
	)

	mode(string) <- "character"
